export type EventNames<T extends IpcEventName> = T | `DISCORD_${T}`;

export type IpcEvent = EventNames<IpcEventName>;

export type IpcInvokeWhitelist = EventNames<"GET_MOUSE_COORDINATES">;

export type IpcSendWhitelist = EventNames<
    | "APP_BADGE_SET"
    | "CHECK_FOR_UPDATES"
    | "NOTIFICATION_CLOSE"
    | "NOTIFICATION_SHOW"
    | "NOTIFICATIONS_CLEAR"
    | "OPEN_EXTERNAL_URL"
    | "QUIT_AND_INSTALL"
    | "SETTINGS_UPDATE_BACKGROUND_COLOR"
    | "SYSTEM_TRAY_SET_ICON"
    | "SYSTEM_TRAY_SET_APPLICATIONS"
    | "THUMBAR_BUTTONS_UPDATE"
    | "TOGGLE_MINIMIZE_TO_TRAY"
    | "TOGGLE_OPEN_ON_STARTUP"
    | "TOGGLE_START_MINIMIZED"
    | "UPDATE_OPEN_ON_STARTUP"
    | "UPDATER_HISTORY_QUERY_AND_TRUNCATE"
    | "UPDATED_QUOTES"
>;

export type Ipc = {
    invoke(ev: IpcInvokeWhitelist, ...args: any[]): Promise<any>;
    on(ev: IpcEvent, listener: (...args: any[]) => void): void;
    send(ev: IpcSendWhitelist, ...args: any[]): void;
};
